import React from 'react';

import profile from '../../assets/images/icons/user.svg';


import Input from "../Input/";
import Textarea from "../Textarea/";

import './styles.css';

export default function TeacherForm() {
    return(
        <article className="teacher-item">
        <header>
            {/* <input type="file" id="myfile" name="myfile"> */}
            <img typeof="file" src={profile} alt="Insira sua foto de perfil"/>
            {/* </input> */}
            <p contentEditable="true">Insira seu nome</p>
        </header>

          <main>
              <fieldset>
              <legend>Seus Dados</legend>

          <Input id="nome"   name="name" label="Nome Completo" />
          <Input id="email"  name="email" label="E-mail" />
          <Input id="zap"    name="zap" label="Celular" />
          <Textarea id="bio" name="bio" label="Biografia" />
          </fieldset>

          <footer>
          <p>
            <img alt="Aviso Importante" />
            Importante <br />
            Preencha todos os dados
          </p>
          <button type="button" onClick={() => cad()} >Salvar cadastro</button>
        </footer>

      </main>
    </article>

    );

function cad(){
    const usuarios = [];
    // console.log("Bom dia");

    const nome =     (document.getElementById("nome") as HTMLInputElement).value;
    const email =     (document.getElementById("email") as HTMLInputElement).value;
    const zap =     (document.getElementById("zap") as HTMLInputElement).value;
    const bio =     (document.getElementById("bio") as HTMLInputElement).value;

    let id = usuarios.length;

    const usuario = {id: id++,nome, email, zap, bio};

    console.log(usuario);

    $.ajax({
      type: 'POST',
      dataType: 'json',
      url: '../database/',
      data: usuario,
      success: function () {

      }
    });


    // let usuariogravado = JSON.parse( window.localStorage.getItem('usuarios'));

    // usuariogravado.push(usuario);

    // window.localStorage.setItem("usuarios", JSON.stringify(usuariogravado));
}
}
